package com.naxtre.laundryservices.kotlin_extension.datetimeimport java.text.SimpleDateFormatimport java.util.*fun calendar(): Calendar {    return Calendar.getInstance().apply {        timeInMillis=System.currentTimeMillis()    }}fun Long.isSunday(): Boolean {    return (Calendar.getInstance().apply { timeInMillis = this@isSunday })        .get(Calendar.DAY_OF_WEEK) == Calendar.SUNDAY}fun Calendar.plusOneDay() {    this.add(Calendar.DATE,1)}fun Calendar.plusDays(days:Int) {    this.add(Calendar.DATE,days)}fun Calendar.isSunday(): Boolean {    return this.get(Calendar.DAY_OF_WEEK) == Calendar.SUNDAY}fun String?.toMillis(format:SimpleDateFormat):Long{    this?:return 0    return format.parse(this)?.time?:0}fun String?.toDateTimeString(from: SimpleDateFormat, to:SimpleDateFormat):String{    return try {        @Suppress("NULLABILITY_MISMATCH_BASED_ON_JAVA_ANNOTATIONS")        to.format(from.parse(this))    }catch (e:Exception){        e.printStackTrace()        ""    }}fun Long.toDateTimeString(to: SimpleDateFormat):String{    return try {        @Suppress("NULLABILITY_MISMATCH_BASED_ON_JAVA_ANNOTATIONS")        to.format(Date(this))    }catch (e:Exception){        e.printStackTrace()        ""    }}